//import OwlCarousel from 'react-owl-carousel';
import { Routes, Route } from "react-router-dom";
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import $ from 'jquery';
import Header from './components/Common/Header/Header';
import Discount from './components/Common/Discount-Secction/Discount';
import Welcome from './components/Main/Welcome-Section/Welcome';
import TopCategory from './components/Main/Top-Category/TopCategory';
import NewArrival from './components/Main/New Arrival/NewArrival';
import OfferArea from './components/Main/Offer Area/OfferArea';
import Testimonials from './components/Main/Testimonials/Testimonials';
import Footer from './components/Common/Footer/Footer';
import Product from './components/Products/Product'
import Main from "./components/Main/Main";
import ProductDesrip from "./components/Product Des/ProductDesrip";
import Cart from "./components/Cart/Cart";
import Checkout from "./components/Checkout/Checkout";
import React from "react";
import {useState} from 'react';

function App() {

	const [cartItems, setCartItems] = useState([]);
    const onAdd = (product) => {
        const exist = Object.keys(cartItems).find(x => x._id === product._id);
        if (exist) {
            setCartItems(Object.keys(cartItems).map(x => x._id === product._id ? { exist, qty: exist.qty + 1 } : x
            )
            )
        } else {
            setCartItems([...cartItems, { ...product, qty: 1 }])
        }
    }

	const onRemove = (product) => {
		const exist = Object.keys(cartItems).find((x) => x.id === product._id);
		if(exist.qty === 1){
			setCartItems(Object.keys(cartItems).filter((x) => x.id !== product._id));
		}else {
			setCartItems(
				Object.keys(cartItems).map((x) => 
				x.id === product._id ? { ...exist, qty: exist.qty - 1 } : x
				)
				)
		}
	}


	return (
		<>
			<Routes>
		<Route path='/' element={ <Main/> } />
        <Route path="/shop" element={ <Product/> } />
        <Route path="/productdetails/:id" element={ <ProductDesrip cartItems={cartItems} onAdd={onAdd} onRemove={onRemove}/> } />
		<Route path="/cart" element={ <Cart cartItems={cartItems} onAdd={onAdd} onRemove={onRemove}/> } />
		<Route path="checkout" element={ <Checkout/> } />

      </Routes>

		</>
	);
}

export default App;
